import { createAsyncThunk, createSlice } from '@reduxjs/toolkit'
import { transferTimeToString } from './CountdownAPI';

const initialState = {
  start: false,
  countSecs: 0,
  countdownString: '00:00'
}


// export const asyncTimer = createAsyncThunk(
//   'countdown/countTimer', 
//   async (sec = 0) => {
//     let result = await countTimer(sec);
//     console.log(result)
//     return result;
//   }
// )

export const countdownSlice = createSlice({
  name: 'countdown',
  initialState,
  reducers: {
    setCountMins: (state, action) => {
      state.start = true;
      console.log(state.start)
      state.countSecs = action.payload * 60;
      state.countdownString = transferTimeToString(state.countSecs);
    },
    countdownExcu: (state) => {
      if( state.countSecs > 0 ) {
        state.countSecs --;
        state.countdownString = transferTimeToString(state.countSecs);
        if ( state.countSecs === 0 ) {
          state.start = false;
        }
      }
    },
  },
})

// Action creators are generated for each case reducer function
export const { countdownExcu, setCountMins } = countdownSlice.actions;

export const countdownString = (state) => state.countdown.countdownString;

export const countdownSecs = (state) => state.countdown.countSecs;

export default countdownSlice.reducer
