import React, { useEffect, useState } from 'react';
import { Container, Row, Col, Button, Input, Label, Table } from 'reactstrap';
import { useSelector, useDispatch } from 'react-redux';
import { 
  setCountMins, 
  countdownExcu,
  countdownSecs, 
  countdownString,
  lotteryDraw
} from '../features/countdown/Countdown';
import '../css/lottery.css';

const Lottery = (props) => {
  const dispatch = useDispatch();
  const [ timer, settimer ] = useState(null);
  const [ inputVal, setInputVal ] = useState(1);
  const sec = useSelector( countdownSecs );
  const _countdownString = useSelector( countdownString );


  const inputHandle = (e) => {
    const mins = e.target.value;
    setInputVal(mins);
  }

  const startHandle = () => {
    if(!timer){
      dispatch( setCountMins( 0.05 ) );
      const countInterval = setInterval(() => {
        dispatch(countdownExcu());
      }, 1000);
      settimer(countInterval)
    }
  }

  useEffect(() => {
    if ( sec === 0 ) {
      //dispatch(lotteryDraw());
      clearInterval(timer);
      settimer(null);
    }
  }, [sec])

  return (  
    <Container>
      <Row>
        <Col xs="12" md="4">
          <h2>抽獎</h2>

        </Col>
        <Col xs="8" md="3">
          <Input type='number'  min={1} value={ inputVal } onChange={ inputHandle }/>
        </Col>
        <Label xs="4" md="1">分鐘</Label>
        <Col xs="12" md="4">
          <Button color="primary" onClick={ startHandle } >Start</Button> 
        </Col>
        <hr/>
        <Col xs="12" md="6">
            <h1>{ _countdownString }</h1>
        </Col>
        <Col xs="12" md="6" >
          <Table bordered striped>
            <tbody className='cusTable'>
              <tr><td>1</td></tr>
              <tr><td>2</td></tr>
              <tr><td>3</td></tr>
              <tr><td>1</td></tr>
              <tr><td>2</td></tr>
              <tr><td>3</td></tr>
              <tr><td>1</td></tr>
              <tr><td>2</td></tr>
              <tr><td>3</td></tr>
              <tr><td>1</td></tr>
              <tr><td>2</td></tr>
              <tr><td>3</td></tr>
            </tbody>
          </Table>
        </Col>

        <Col xs="12" md="6">
          3
        </Col>

        <Col xs="12" md="6">
          4
        </Col>
      </Row>
    </Container>

  )
}

export default Lottery;
