import React, { useEffect, useState } from 'react';
import { Container, Row, Col, Button, Input, Label } from 'reactstrap';
import { useSelector, useDispatch } from 'react-redux';
import { 
  setCountMins, 
  countdownExcu,
  countdownSecs, 
  countdownString
} from '../features/countdown/Countdown';

const Lottery = (props) => {
  const [ inputVal, setInputVal ] = useState(0);
  const [ getSec, setSec ] = useState(0);
  const _countdownString = useSelector( countdownString );
  const sec = useSelector( countdownSecs );
  const dispatch = useDispatch();
  let timer = null;

  const inputHandle = (e) => {
    const mins = e.target.value;
    setInputVal(mins);
  }

  const startHandle = (e) => {
    if(!timer){
      dispatch( setCountMins( inputVal ) );
      timer = setInterval(() => {
        dispatch(countdownExcu());
        console.log(getSec)
        //if( !sec ) clearInterval( timer );
      }, 1000);
    }
  }

  useEffect(() => {
    setSec(sec);
  }, [sec])

  return (  
    <Container>
      <Row>
        <Col xs="12" md="4">
          <h2>抽獎</h2>
        </Col>
        <Col xs="8" md="3">
          <Input type='number'  min={1} value={ inputVal } onChange={ inputHandle }/>
        </Col>
        <Label xs="4" md="1">分鐘</Label>
        <Col xs="12" md="4">
          <Button color="primary" onClick={ startHandle } >Start</Button> 
        </Col>
        <br/><hr></hr>
        <Col xs="12" md="6">
            <h1>{ _countdownString }</h1>
        </Col>
        <Col xs="12" md="6">
          2
        </Col>

        <Col xs="12" md="6">
          3
        </Col>

        <Col xs="12" md="6">
          4
        </Col>
      </Row>
    </Container>

  )
}

export default Lottery;
