import { createSlice } from '@reduxjs/toolkit'

let titokTimer = null;

const initialState = {
  timeoutValue: 0,
  countdownString: '00:00'
}

const timer = (miliSec) => {
  return new Promise((resolve) => {
    titokTimer = setInterval(() => {
      resolve(123)
    }, miliSec);
  })
}

export const countdownSlice = createSlice({
  name: 'countdown',
  initialState,
  reducers: {
    countdown: async(state, action) => {
      state.timeoutValue = action.payload;
      let _val = await timer(action.payload*1000);
      console.log(_val)
    }
  },
})

// Action creators are generated for each case reducer function
export const { countdown } = countdownSlice.actions;

export const countdownString = (state) => state.countdown.countdownString;

export default countdownSlice.reducer
