import { createAsyncThunk, createSlice } from '@reduxjs/toolkit'
import { countTimer } from './CountdownAPI';

const initialState = {
  countSecs: 0,
  countdownString: '00:00'
}


export const asyncTimer = createAsyncThunk(
  'countdown/countTimer', 
  async (sec = 0) => {
    let result = await countTimer(sec);
    console.log(result)
    return result;
  }
)

export const countdownSlice = createSlice({
  name: 'countdown',
  initialState,
  reducers: {
    countdown: (state, action) => {
      // state.timeoutValue = action.payload;
      // let _val = await timer(action.payload*1000);
      console.log(action.payload)
    },
    countdownExcu: (state) => {
      if( state.countSecs > 0 ) {
        state.countSecs --;
        state.countdownString = 1
      }
    },
    setCountMins: (state, action) => {
      state.countSecs = action.payload * 60;
    }
  },
})

// Action creators are generated for each case reducer function
export const { countdown, countdownExcu, setCountMins } = countdownSlice.actions;

export const countdownString = (state) => state.countdown.countdownString;

export default countdownSlice.reducer
