import React, { useState } from 'react';
import { Container, Row, Col, Button, Input } from 'reactstrap';
import { useSelector, useDispatch } from 'react-redux';
import { countdown, countdownString } from '../features/countdown/Countdown';

const Lottery = (props) => {
  const [ inputVal, setInputVal ] = useState(0);
  const _countdownString = useSelector(countdownString);
  const dispatch = useDispatch();

  const inputHandle = (e) => {
    const mins = e.target.value;
    console.log(mins);
    setInputVal(mins);
  }

  return (  
    <Container>
      <Row>
        <Col xs="12" md="4">
          <h2>抽獎時間</h2>
        </Col>
        <Col xs="12" md="4">
          <Input type='number' min={1} value={inputVal} onChange={inputHandle}/>
        </Col>
        <Col xs="12" md="4">
          <Button color="primary" onClick={() => dispatch(countdown(inputVal))} >Start</Button> 
        </Col>
        <hr></hr>
        <Col xs="12" md="6">
            <h1>{_countdownString}</h1>
        </Col>
        <Col xs="12" md="6">
          2
        </Col>

        <Col xs="12" md="6">
          3
        </Col>

        <Col xs="12" md="6">
          4
        </Col>
      </Row>
    </Container>

  )
}

export default Lottery;
